    from app.icons import inject_bootstrap_icons_css, icon, svg_icon

    # At the top of your Streamlit app:
    inject_bootstrap_icons_css()

    # Then use icons:
    st.markdown(icon("house") + " Home", unsafe_allow_html=True)
