def svg_icon(name: str, size: int = 16, color: str = "currentColor") -> str:
    """Get an SVG icon as HTML string with customizable size and color.

    Args:
        name: Icon name (e.g., "house", "cart", "calendar")
        size: Icon size in pixels (default: 16)
        color: Icon color (default: "currentColor" to inherit text color)

    Returns:
        HTML string containing the SVG icon, or empty string if icon not found.

    Example:
        st.markdown(svg_icon("house", size=24, color="#4CAF50"), unsafe_allow_html=True)
    """
    if name not in ICONS:
        return ""

    svg = ICONS[name]
    # Replace size
    svg = svg.replace('width="16"', f'width="{size}"')
    svg = svg.replace('height="16"', f'height="{size}"')
    # Replace color
    svg = svg.replace('fill="currentColor"', f'fill="{color}"')

    return svg


def load_svg_from_file(filepath: str | Path, size: int = 16, color: str = "currentColor") -> str:
    """Load an SVG icon from a file and return it as HTML string.

    Args:
        filepath: Path to the SVG file
        size: Icon size in pixels
        color: Icon color

    Returns:
        HTML string containing the SVG icon, or empty string if file not found.
    """
    path = Path(filepath)
    if not path.exists():
        return ""

    svg = path.read_text()
    svg = svg.replace('width="16"', f'width="{size}"')
    svg = svg.replace('height="16"', f'height="{size}"')
    svg = svg.replace('fill="currentColor"', f'fill="{color}"')

    return svg


def inject_bootstrap_icons_css() -> str:
    """Return the CSS needed to use Bootstrap Icons font via CDN.

    Usage:
        import streamlit as st
        from app.icons import inject_bootstrap_icons_css

        st.markdown(inject_bootstrap_icons_css(), unsafe_allow_html=True)
    """
    return """
<link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap-icons@1.13.1/font/bootstrap-icons.min.css">
<style>
    .bi { vertical-align: -0.125em; }
</style>
"""


def icon(name: str, size: str = "1em") -> str:
    """Get a Bootstrap Icon using the icon font (requires inject_bootstrap_icons_css).

    Args:
        name: Icon name without the "bi-" prefix (e.g., "house", "cart")
        size: CSS font-size value (default: "1em")

    Returns:
        HTML string containing the icon element.

    Example:
        st.markdown(inject_bootstrap_icons_css(), unsafe_allow_html=True)
        st.markdown(icon("house") + " Home", unsafe_allow_html=True)
    """
    return f'<i class="bi bi-{name}" style="font-size: {size};"></i>'


