def emoji_to_svg(emoji: str, size: int = 16, color: str = "currentColor") -> str:
    """Convert an emoji to its Bootstrap Icon SVG equivalent.

    Args:
        emoji: The emoji character to convert
        size: Icon size in pixels
        color: Icon color

    Returns:
        SVG HTML string, or the original emoji if no mapping exists.
    """
    icon_name = EMOJI_TO_ICON.get(emoji)
    if icon_name:
        return svg_icon(icon_name, size, color)
    return emoji
